import React, { useState, useEffect } from 'react';

// Material-ui
import { makeStyles } from '@material-ui/core/styles';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import FolderIcon from '@material-ui/icons/Folder';
import IconButton from '@material-ui/core/IconButton';
import Modal from '@material-ui/core/Modal';
import TextField from '@material-ui/core/TextField';
import DeleteIcon from '@material-ui/icons/Delete';
import Typography from '@material-ui/core/Typography';
import AddCircleIcon from '@material-ui/icons/AddCircle';


import axios from 'axios';
import { useSelector, useDispatch } from 'react-redux';
import { notesInFolder } from '../actions/noteActions';

function rand() {
    return Math.round(Math.random() * 20) - 10;
}

function getModalStyle() {
    const top = 50 + rand();
    const left = 50 + rand();

    return {
        top: `${top}%`,
        left: `${left}%`,
        transform: `translate(-${top}%, -${left}%)`,
    };
}

const useStyles = makeStyles((theme) => ({
    addFolder: {
        color: '#fff',
        fontSize: 50,
        "&hover": {
            backgroundColor: '#53a374'
        }
    },
    folder: {
        borderBottom: '0.2px solid #fff',
    },
    modal: {
        position: 'absolute',
        width: '20vw',
        height: '20vh',
        backgroundColor: theme.palette.background.paper,
        padding: theme.spacing(2, 4, 3),
    },
    form: {
        display: 'block',
    },
}));

const Folders = () => {
    const classes = useStyles();

    const [rendered, setRendered] = useState(false);
    const [open, setOpen] = useState(false);
    const [modalStyle] = useState(getModalStyle);
    const [folders, setFolders] = useState([]);
    const [folderName, setfolderName] = useState('');

    const notes = useSelector((state) => state.notes);
    const dispatch = useDispatch();

    const handleOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        axios.post('/addFolder', {
            folder: folderName,
        });
    };

    const handleChange = (event) => {
        setfolderName(event.target.value);
    };

    const getNotes = (event) => {
        event.persist();
        dispatch(notesInFolder(event.currentTarget.dataset.value));
    };

    useEffect(() => {
        axios
            .get('/folders')
            .then((res) => {
                return res.data;
            })
            .then((res) => {
                setFolders(res.folders);
            });
    });

    const modalBody = (
        <div style={modalStyle} className={classes.modal}>
            <form onSubmit={handleSubmit}>
                <TextField
                    id="outlined-basic"
                    label="Folder Name"
                    variant="outlined"
                    onChange={handleChange}
                />
                <button type="submit" className={classes.form}>
                    Submit
                </button>
            </form>
        </div>
    );

    return (
        <div>
            <List style={{ marginTop: '0px' }}>
                {folders.map((folderName) => {
                    return (
                        <ListItem
                            button
                            onClick={getNotes}
                            data-value={folderName}
                        >
                            <ListItemIcon>
                                <FolderIcon style={{ color: '#0166FF' }} />
                            </ListItemIcon>
                            <ListItemText
                                primary={<Typography variant='h6'>{folderName}</Typography>}
                                value={folderName}
                                style={{ color: '#fff', fontWeight: 'bold' }}
                            />
                            <IconButton edge="end" aria-label="delete">
                                <DeleteIcon style={{ color: '#0166FF' }} />
                            </IconButton>
                        </ListItem>
                    );
                })}
                <IconButton onClick={handleOpen} >
                    <AddCircleIcon className={classes.addFolder}/>
                </IconButton>
                <Typography display="inline" variant="button">
                    Add Folder
                </Typography>
            </List>
            <Modal open={open} onClose={handleClose}>
                {modalBody}
            </Modal>
        </div>
    );
};

export default Folders;
