import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { getSelectedNote, notesInFolder } from '../actions/noteActions';
import axios from 'axios';

import { makeStyles } from '@material-ui/core/styles';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import TextField from '@material-ui/core/TextField';

const useStyles = makeStyles((theme) => ({
    addNote: {
        color: '#000',
        textAlign: 'center',
        backgroundColor: '#fff',
    },
}));

const Notes = () => {
    const classes = useStyles();

    const notes = useSelector((state) => state.notes.notes);
    const dispatch = useDispatch();

    const displayNote = (event) => {
        event.persist();
        dispatch(getSelectedNote(event.currentTarget.dataset.value, notes));
    };

    const addNote = () => {
        const folder = notes[0].folder;
        axios
            .post('/addNote', {
                folder: folder,
            })
            .then((res) => {
                console.log(res);
            });
        dispatch(notesInFolder(folder));
    };

    return (
        <div>
            <List>
                {/* <ListItem button className={classes.addNote}>
                    <ListItemText
                        primary="Add Note"
                        onClick={addNote}
                    />
                </ListItem> */}
                <form noValidate autoComplete="off ">
                    <TextField id="standard-basic" label="Standard" />
                </form>
                {notes.length > 0 ? (
                    notes.map((note) => {
                        return (
                            <ListItem
                                button
                                onClick={displayNote}
                                data-value={note._id}
                            >
                                <ListItemText
                                    primary={note.title}
                                    style={{ color: '#000' }}
                                />
                            </ListItem>
                        );
                    })
                ) : (
                    <p>No notes</p>
                )}
            </List>
        </div>
    );
};

export default Notes;
