import { CUR_FOLDER_NOTES, SELECTED_NOTE } from './types'
import axios from 'axios'

export const notesInFolder = (folder) => dispatch => {
    axios.post('/notesInFolder', {
        folder: folder
    }).then((res) => {
        return res.data
    }).then((notes) => {
        dispatch({
            type: CUR_FOLDER_NOTES,
            payload: notes
        })
    })
}

export const noteContent = (content) => {
    console.log('In note content')
    return { type: SELECTED_NOTE, payload: content}
}
