import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { getSelectedNote, notesInFolder } from '../actions/noteActions';
import axios from 'axios';

import { makeStyles } from '@material-ui/core/styles';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import TextField from '@material-ui/core/TextField';
import InputAdornment from '@material-ui/core/InputAdornment';
import IconButton from '@material-ui/core/IconButton';
import SearchIcon from '@material-ui/icons/Search';
import AddCircleIcon from '@material-ui/icons/AddCircle';
import Typography from '@material-ui/core/Typography'

const useStyles = makeStyles((theme) => ({
    searchNote: {
        textAlign: 'center',
    },
    addNote: {
        color: '#000',
        textAlign: 'center',
        backgroundColor: '#fff',
    },
    note: {

    },
    noNotes: {
        marignLeft: '1vw',
        marginBottom: '1vh',
        marginTop: '1vh'
    }
}));

const Notes = () => {
    const classes = useStyles();

    const notes = useSelector((state) => state.notes.notes);
    const dispatch = useDispatch();

    const displayNote = (event) => {
        event.persist();
        dispatch(getSelectedNote(event.currentTarget.dataset.value, notes));
    };

    const addNote = () => {
        const folder = notes[0].folder;
        axios
            .post('/addNote', {
                folder: folder,
            })
            .then((res) => {
                console.log(res);
            });
        dispatch(notesInFolder(folder));
    };

    return (
        <div>
            <List>
                {/* <ListItem button className={classes.addNote}>
                    <ListItemText
                        primary="Add Note"
                    />
                </ListItem> */}
                <form
                    noValidate
                    autoComplete="off "
                    className={classes.searchNote}
                >
                    <TextField
                        label="Search Notes"
                        InputProps={{
                            startAdornment: (
                                <InputAdornment position="start">
                                    <SearchIcon />
                                </InputAdornment>
                            ),
                        }}
                    />
                </form>
                {notes.length > 0 ? (
                    notes.map((note) => {
                        return (
                            <ListItem
                                button
                                onClick={displayNote}
                                data-value={note._id}
                            >
                                <ListItemText
                                    primary={note.title}
                                    style={{ color: '#000' }}
                                />
                            </ListItem>
                        );
                    })
                ) : (
                    <Typography variant="body-1" display="block" className={classes.noNotes}>No Notes</Typography>
                )}
                <IconButton onClick={addNote} className={classes.addNote}>
                    <AddCircleIcon style={{ fontSize: 50 }} />
                </IconButton>
                <Typography variant="body-1" display="inline">Add Note</Typography>
            </List>
        </div>
    );
};

export default Notes;
