import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
    editorLangForm: {
        margin: theme.spacing(1),
        width: '10vw',
    },
    marks: {
        fontSize: '1.35vw',
        backgroundColor: '#fff',
        '&:hover': {
            backgroundColor: '#bdbdbd',
        },
        borderRadius: '5px',
    },
    marksSelected: {
        fontSize: '1.35vw',
        backgroundColor: '#bdbdbd',
        borderRadius: '5px'
    },
    markButtons: {
        color: '#000',
        backgroundColor: '#fff',
        '&:hover': {
            backgroundColor: '#fff',
        },
    },
    addEditor: {
        height: '40px',
        width: '78px',
        backgroundColor: '#000',
        '&:hover': {
            backgroundColor: '#bdbdbd',
        },
    },
    addEditorTxt: {
        lineHeight: '1.4 !important',
        color: '#fff',
    },
}));

export default useStyles;
