import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
    editorLangForm: {
        marginLeft: '10px',
        marginRight: '10px',
        width: '9vw',
        minWidth: '10px'
    },
    marks: {
        fontSize: 20,
        backgroundColor: '#fff',
        '&:hover': {
            backgroundColor: '#bdbdbd',
        },
        borderRadius: '5px',
    },
    marksSelected: {
        fontSize: '1.4vw',
        backgroundColor: '#bdbdbd',
        borderRadius: '5px'
    },
    markButtons: {
        color: '#000',
        backgroundColor: '#fff',
        '&:hover': {
            backgroundColor: '#fff',
        },
    },
    addEditor: {
        height: '48px',
        width: '5vw',
        backgroundColor: '#000',
        '&:hover': {
            backgroundColor: '#bdbdbd',
        },
    },
    addEditorTxt: {
        lineHeight: '1.4 !important',
        color: '#fff',
    },
}));

export default useStyles;
