import React, { useState, useEffect } from 'react';
import axios from 'axios'

const withAuth = () => {
    const [loading, setLoading] = useState(true);
    const [auth, setAuth] = useState(false);

    useEffect(() => {
        axios.get('/checkAuth').then((res) => {
            if (res.auth) {
                setAuth(true)
                setLoading(false)
            }
        })
    });

    return auth
};

export default withAuth;
